import React, { useEffect, useState } from 'react';
import {Input, Button, Grid, Table} from 'semantic-ui-react';

import { useSubstrate } from './substrate-lib';



function Main(props){
    const { api } = useSubstrate();

    return(
        <div>
            <Grid fluid>
                <Input placeholder="Block..."/>
                <Button onClick=""> Search for Block</Button>
            </Grid>
            <Table celled>
            <Table.Row>
                    <Table.Cell> Block Number </Table.Cell>
                    <Table.Cell>{}</Table.Cell>
                </Table.Row>
                <Table.Row>
                    <Table.Cell> Block Hash </Table.Cell>
                    <Table.Cell> {}</Table.Cell>
                </Table.Row>
                <Table.Row>
                    <Table.Cell> Parent Hash </Table.Cell>
                    <Table.Cell> {}</Table.Cell>
                </Table.Row>
                <Table.Row>
                    <Table.Cell> Extrinsic Root </Table.Cell>
                    <Table.Cell> {}</Table.Cell>
                </Table.Row>
                <Table.Row>
                    <Table.Cell> State Root </Table.Cell>
                    <Table.Cell> {}</Table.Cell>
                </Table.Row>
            </Table>
        </div>
    );
}

export default function BlockSearchInfo(props){
    const{api} = useSubstrate();
    return api.rpc &&
    api.rpc.chain &&
    api.rpc.chain.subscribeNewHeads  ? (
        <Main {...props} />
    ) : null;
}
