import React, { useEffect, useState } from 'react';
import { Statistic, Grid, Card, Icon} from 'semantic-ui-react';

import { useSubstrate } from './substrate-lib';



function BlockInfo(props){
    const { api } = useSubstrate();
    const [blockNumber, setBlockNumber] = useState(0);
    const [blockHash, setBlockHash] = useState(0);
    const [parrentHash, setParrentHash] = useState(0);

    const getInfo = async () =>{
        const unsubscribe = await api.rpc.chain.subscribeNewHeads((header) => {
            //console.log(`Chain is at block: #${header.hash}`);\
            setBlockNumber(header.number);
            setBlockHash(header.hash.toHuman());
            setParrentHash(header.parrentHash.toHuman());
            //console.log(blockHash)
            
        });
    }
    getInfo();
    
    return(
    <div>
        BlockNumber: {blockNumber}
        <br/>
        Blockhash: {blockHash}
        <br/>
        Parrenthash: {parrentHash}
    </div>
         

    );

}

export default BlockInfo
