import React, { useEffect, useState } from 'react';
import {Input, Button, Form, Table} from 'semantic-ui-react';

import { useSubstrate } from './substrate-lib';



function Main(props){
    const { api } = useSubstrate();

    const [blockNumber, setBlockNumber] = useState();
    const [block, setBlock] = useState();

    const searchBlock = async (blockNumber) =>{
        try{
            const blockHash = await this.api.rpc.chain.getBlockHash(blockNumber);
            console.log(blockHash);
            const header = await api.rpc.chain.getHeader(blockHash);
            setBlock(header);

        }
        catch(error){
            console.error(error);
        }
    }
    
    return(
        <div>
            <Form onSubmit = {async (e, { value }) => await searchBlock(blockNumber)}>
                <Input placeholder="Block Number..."/>
                <Button onClick={SearchBlock()}> Search for Block</Button>
            </Form>
            <Table celled>
            <Table.Row>
                    <Table.Cell> Block Number </Table.Cell>
                    <Table.Cell>{}</Table.Cell>
                </Table.Row>
                <Table.Row>
                    <Table.Cell> Block Hash </Table.Cell>
                    <Table.Cell> {}</Table.Cell>
                </Table.Row>
                <Table.Row>
                    <Table.Cell> Parent Hash </Table.Cell>
                    <Table.Cell> {}</Table.Cell>
                </Table.Row>
                <Table.Row>
                    <Table.Cell> Extrinsic Root </Table.Cell>
                    <Table.Cell> {}</Table.Cell>
                </Table.Row>
                <Table.Row>
                    <Table.Cell> State Root </Table.Cell>
                    <Table.Cell> {}</Table.Cell>
                </Table.Row>
            </Table>
        </div>
    );
}

function SearchBlock(){
    console.log("Search");
}

export default function BlockSearchInfo(props){
    const{api} = useSubstrate();
    return api.rpc &&
    api.rpc.chain ? (
        <Main {...props} />
    ) : null;
}
