import React, { useEffect, useState } from 'react';
import { Statistic, Grid, Card, Icon} from 'semantic-ui-react';

import { useSubstrate } from './substrate-lib';



function BlockInfo(props){
    const { api } = useSubstrate();
    const [blockHeader, setBlocHeader] = useState(0);


    const getInfo = async () =>{
        const unsubscribe = await api.rpc.chain.subscribeNewHeads((header) => {
            //console.log(`Chain is at block: #${header.hash}`);\
            setBlockHeader(header);
            //console.log(blockHash)
            
        });
    }
    getInfo();
    
    return(
    <div>
        Block Number: {blockHeader.number.toNumber()}
        <br/>
        Block hash: {blockHeader.blockhash.toHuman()}
        <br/>
        Parent hash: {blockHeader.parenthash.toHuman()}
    </div>
    );

}

export default BlockInfo
