import React, { useEffect, useState } from 'react';
import { Statistic, Grid, Card, Icon} from 'semantic-ui-react';

import { useSubstrate } from './substrate-lib';



function Main(props){
    const { api } = useSubstrate();

    const[Block, setBlock] = useState();

    useEffect(()=> {
        let unsubscribe = null;

        api.rpc.chain.subscribeNewHeads((header) => {
            setBlock(header);
            console.log(`Chain is at block: #${header.number}`);
        }).then(unsub =>{
            unsubscribe = unsub;
        }).catch(console.error);

        return () => unsubscribe && unsubscribe();
    }, []);

    /*
    const getInfo = async () =>{
        const unsubscribe = await api.rpc.chain.subscribeNewHeads((header) => {
            //console.log(`Chain is at block: #${header.hash}`);\
            setBlockNumber(header.number.toNumber());
            setBlockHash(header.hash.toHuman());
            setParrentHash(header.parentHash.toHuman());
            setExtrinsicsRoot(header.extrinsicsRoot.toHuman());
            setStateRoot()
            //console.log(blockHash)
            
        });
    }
    getInfo();*/
    
    return(
         <Grid fluid>
            {Block &&<Grid.Column>
                <Grid.Row>
                    Block Number: {Block.number.toNumber()}
                </Grid.Row>
                <Grid.Row>
                Block Number: {Block.number.toNumber()}
                </Grid.Row>
            </Grid.Column>}
        </Grid>
    );

}

export default function BlockInfo(props){
    const{api} = useSubstrate();
    return api.rpc &&
    api.rpc.chain &&
    api.rpc.chain.subscribeNewHeads  ? (
        <Main {...props} />
    ) : null;
}
